sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // 1. Create an object to store rows from a table
    var grNeedit = new GlideRecord('x_58872_needit_needit');

    // 2. Build query
    grNeedit.addQuery('state', 'NOT IN', 3, 4, 7);
    grNeedit.addQuery('u_requested_for', '=', current.getValue("u_requested_for"));

    // 3. Execute query 
    grNeedit.query();

    // Initialize message to list Requested For's other active requests
    var msg = "Requested For's other active requests: ";

    // 4. Process returned records
    while (grNeedit.next()) {
        // add a new line to the message for each of the Requested For's other active requests
        msg = msg + grNeedit.getValue('number') + ": " + grNeedit.getValue('short_description') + " | ";
    }

    gs.addInfoMessage(msg);

})(current, previous);
